// 驗證新增卡片表單（標題必填、長度限制）
function validateAddCard() {
  const errors = {}
  if (!addCardDialog.title) {
    errors.title = '卡片標題不能為空'
  } else if (addCardDialog.title.length > 40) {
    errors.title = '標題長度不能超過 40 字'
  }
  addCardDialog.errors = errors
  return Object.keys(errors).length === 0
}

