  if (!validateAddCard()) return
  board.addCard(
    addCardDialog.listId,
    {
      title: addCardDialog.title,
      description: addCardDialog.description,
      date: addCardDialog.date,
      images: addCardDialog.images.map(img => img.url)
    }
  )
  closeAddCardDialog()
}

