const STORAGE_KEY = 'trello_lists_v3'

/**
 * 從 localStorage 讀取 lists 快取
 * @returns {Array|null}
 */
function loadLists() {
  try {
    const data = localStorage.getItem(STORAGE_KEY)
    return data ? JSON.parse(data) : null
  } catch (e) {
    return null
  }
}

