// 關閉卡片詳情
function closeDetail() { detailDialog.visible = false }
// 詳情頁直接更新卡片
function onDetailUpdate(item) { boardStore.updateItemByTitle(detailDialog.listTitle, item) }

// 卡片詳情彈窗狀態
const detailDialog = reactive({ visible: false, item: {}, listTitle: '' })
// 編輯/新增卡片表單彈窗狀態
